import React, { useState } from 'react';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import { navigateToUrl } from "single-spa";

const drawerWidth = 240;

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            display: 'flex',
        },
        appBar: {
            zIndex: theme.zIndex.drawer + 1,
        },
        drawer: {
            width: drawerWidth,
            flexShrink: 0,
        },
        drawerPaper: {
            width: drawerWidth,
        },
        drawerContainer: {
            overflow: 'auto',
        },
        content: {
            flexGrow: 1,
            padding: theme.spacing(3),
        },
    }),
);

export default function Nav() {
    const classes = useStyles();
    const [selectedItem, setSelectedItem] = useState<string>("none");

    const handleClick = (url: string): void => {
        setSelectedItem(url.split("/")[1]);

        navigateToUrl(url);
    }

    return (
        <div className={classes.root}>
            <CssBaseline />
            <AppBar position="fixed" className={classes.appBar}>
                <Toolbar>
                    <Typography variant="h6" noWrap>
                        Portal Facturador Cloud lo mas grande
                    </Typography>
                </Toolbar>
            </AppBar>
            <Drawer
                className={classes.drawer}
                variant="permanent"
                classes={{
                    paper: classes.drawerPaper,
                }}
            >
                <Toolbar />
                <div className={classes.drawerContainer}>
                    <List>
                        <ListItem button onClick={() => handleClick('/react')} selected={selectedItem === "react"}>
                            <ListItemText primary="App React" />
                        </ListItem>
                        <ListItem button onClick={() => handleClick('/pagar')} selected={selectedItem === "pagar"}>
                            <ListItemText primary="App pagar" />
                        </ListItem>
                        <ListItem button onClick={() => handleClick('/listar')} selected={selectedItem === "listar"}>
                            <ListItemText primary="App listar" />
                        </ListItem>
                    </List>
                </div>
            </Drawer>
            <main className={classes.content}>
                <Toolbar />
                <div id="single-spa-application:app-react"></div>
                <div id="single-spa-application:app-pagar"></div>
            </main>
        </div>
    );
}
